                    {
                        // Create the item and add to the list.
                        string cvalue = null;
                        string fvalue = null;
                        if (itemValue.ContactIDSpecified) 
                        {
                            cvalue = itemValue.ContactID.ToString();
                            if (counter < maxItems && this.ContactIDFilter1.Items.FindByValue(cvalue) == null)
                            {
                                    
                                Boolean _isExpandableNonCompositeForeignKey = LinkTableTable.Instance.TableDefinition.IsExpandableNonCompositeForeignKey(LinkTableTable.ContactID);
                                if(_isExpandableNonCompositeForeignKey && LinkTableTable.ContactID.IsApplyDisplayAs)
                                     fvalue = LinkTableTable.GetDFKA(itemValue, LinkTableTable.ContactID);
                                if ((!_isExpandableNonCompositeForeignKey) || (String.IsNullOrEmpty(fvalue)))
                                     fvalue = itemValue.Format(ContactsTable.ContactID);
                                   					
                                if (fvalue == null || fvalue.Trim() == "") fvalue = cvalue;

                                if (fvalue == null) {
                                    fvalue = "";
                                }

                                fvalue = fvalue.Trim();

                                if ( fvalue.Length > 50 ) {
                                   fvalue = fvalue.Substring(0, 50) + "...";
                                }

                                ListItem dupItem = this.ContactIDFilter1.Items.FindByText(fvalue);
								
                                if (dupItem != null) {
                                    listDuplicates.Add(fvalue);
                                    if (!string.IsNullOrEmpty(dupItem.Value))
                                    {
                                        dupItem.Text = fvalue + " (ID " + dupItem.Value.Substring(0, Math.Min(dupItem.Value.Length,38)) + ")";
                                    }
                                }

                                ListItem newItem = new ListItem(fvalue, cvalue);
                                this.ContactIDFilter1.Items.Add(newItem);

                                if (listDuplicates.Contains(fvalue) &&  !string.IsNullOrEmpty(cvalue)) {
                                    newItem.Text = fvalue + " (ID " + cvalue.Substring(0, Math.Min(cvalue.Length,38)) + ")";
                                }

                                counter += 1;
                            }
                        }
                    }
                    pageNum++;
                }
                while (itemValues.Length == maxItems && counter < maxItems);
            }
        
                      
            try
            {
      
                
                // Set the selected value.
                MiscUtils.SetSelectedValue(this.ContactIDFilter1, selectedValue);
                
            }
            catch
            {
            }
            
            
            if (this.ContactIDFilter1.SelectedValue != null && this.ContactIDFilter1.Items.FindByValue(this.ContactIDFilter1.SelectedValue) == null)
                this.ContactIDFilter1.SelectedValue = null;
                           
        }
            
