        protected virtual void PopulateDescriptionFilter3(string selectedValue, int maxItems)
                    
        {
        
            
            //Setup the WHERE clause.
                        
            this.DescriptionFilter3.Items.Clear();
            WhereClause wc = this.CreateWhereClause_DescriptionFilter3();            
            			  			
            // Set up the WHERE and the ORDER BY clause by calling the CreateWhereClause_DescriptionFilter3 function.
            // It is better to customize the where clause there.
             
            // Setup the static list items        
            
                // Add the All item.
                this.DescriptionFilter3.Items.Insert(0, new ListItem(this.Page.GetResourceValue("Txt:All", "IPv5"), "--ANY--"));
              
            
            
            OrderBy orderBy = new OrderBy(false, false);
            orderBy.Add(RentReviewsTable.Description, OrderByItem.OrderDir.Asc);                
            
            
            string[] values = new string[0];
            if (wc.RunQuery)
            {
            
                values = RentReviewsTable.GetValues(RentReviewsTable.Description, wc, orderBy, maxItems);
            
            }
            
            ArrayList listDuplicates = new ArrayList();
            foreach (string cvalue in values)
            {
            // Create the item and add to the list.
            string fvalue;
            if ( RentReviewsTable.Description.IsColumnValueTypeBoolean()) {
                    fvalue = cvalue;
                }else {
                    fvalue = RentReviewsTable.Description.Format(cvalue);
                }
                if (fvalue == null) {
                    fvalue = "";
                }

                fvalue = fvalue.Trim();

                if ( fvalue.Length > 50 ) {
                    fvalue = fvalue.Substring(0, 50) + "...";
                }

                ListItem dupItem = this.DescriptionFilter3.Items.FindByText(fvalue);
								
                if (dupItem != null) {
                    listDuplicates.Add(fvalue);
                    if (!string.IsNullOrEmpty(dupItem.Value))
                    {
                        dupItem.Text = fvalue + " (ID " + dupItem.Value.Substring(0, Math.Min(dupItem.Value.Length,38)) + ")";
                    }
                }

                ListItem newItem = new ListItem(fvalue, cvalue);
                this.DescriptionFilter3.Items.Add(newItem);

                if (listDuplicates.Contains(fvalue) &&  !string.IsNullOrEmpty(cvalue)) {
                    newItem.Text = fvalue + " (ID " + cvalue.Substring(0, Math.Min(cvalue.Length,38)) + ")";
                }
            }

                          
            try
            {
      
                
                // Set the selected value.
                MiscUtils.SetSelectedValue(this.DescriptionFilter3, selectedValue);
                
            }
            catch
            {
            }
            
            
            if (this.DescriptionFilter3.SelectedValue != null && this.DescriptionFilter3.Items.FindByValue(this.DescriptionFilter3.SelectedValue) == null)
                this.DescriptionFilter3.SelectedValue = null;
                           
        }
            
