        protected virtual void PopulateDescriptionFilter5(string selectedValue, int maxItems)
                    
        {
        
            
            //Setup the WHERE clause.
                        
            this.DescriptionFilter5.Items.Clear();
            WhereClause wc = this.CreateWhereClause_DescriptionFilter5();            
            			  			
            // Set up the WHERE and the ORDER BY clause by calling the CreateWhereClause_DescriptionFilter5 function.
            // It is better to customize the where clause there.
             
            // Setup the static list items        
            
                // Add the All item.
                this.DescriptionFilter5.Items.Insert(0, new ListItem(this.Page.GetResourceValue("Txt:All", "IPv5"), "--ANY--"));
              
            
            
            OrderBy orderBy = new OrderBy(false, false);
            orderBy.Add(TermExpiresTable.Description, OrderByItem.OrderDir.Asc);                
            
            
            string[] values = new string[0];
            if (wc.RunQuery)
            {
            
                values = TermExpiresTable.GetValues(TermExpiresTable.Description, wc, orderBy, maxItems);
            
            }
            
            ArrayList listDuplicates = new ArrayList();
            foreach (string cvalue in values)
            {
            // Create the item and add to the list.
            string fvalue;
            if ( TermExpiresTable.Description.IsColumnValueTypeBoolean()) {
                    fvalue = cvalue;
                }else {
                    fvalue = TermExpiresTable.Description.Format(cvalue);
                }
                if (fvalue == null) {
                    fvalue = "";
                }

                fvalue = fvalue.Trim();

                if ( fvalue.Length > 50 ) {
                    fvalue = fvalue.Substring(0, 50) + "...";
                }

                ListItem dupItem = this.DescriptionFilter5.Items.FindByText(fvalue);
								
                if (dupItem != null) {
                    listDuplicates.Add(fvalue);
                    if (!string.IsNullOrEmpty(dupItem.Value))
                    {
                        dupItem.Text = fvalue + " (ID " + dupItem.Value.Substring(0, Math.Min(dupItem.Value.Length,38)) + ")";
                    }
                }

                ListItem newItem = new ListItem(fvalue, cvalue);
                this.DescriptionFilter5.Items.Add(newItem);

                if (listDuplicates.Contains(fvalue) &&  !string.IsNullOrEmpty(cvalue)) {
                    newItem.Text = fvalue + " (ID " + cvalue.Substring(0, Math.Min(cvalue.Length,38)) + ")";
                }
            }

                          
            try
            {
      
                
                // Set the selected value.
                MiscUtils.SetSelectedValue(this.DescriptionFilter5, selectedValue);
                
            }
            catch
            {
            }
            
            
            if (this.DescriptionFilter5.SelectedValue != null && this.DescriptionFilter5.Items.FindByValue(this.DescriptionFilter5.SelectedValue) == null)
                this.DescriptionFilter5.SelectedValue = null;
                           
        }
            
