            // Save button.  The button handler should Start/Commit/End a transaction.
              
            foreach (MMContractsTableControlRow recCtl in this.GetRecordControls())
            {
        
                if (this.InDeletedRecordIds(recCtl)) {
                    // Delete any pending deletes. 
                    recCtl.Delete();
