            }
            
            
            if (this.DescriptionFilter1.SelectedValue != null && this.DescriptionFilter1.Items.FindByValue(this.DescriptionFilter1.SelectedValue) == null)
                this.DescriptionFilter1.SelectedValue = null;
                           
        }
            
